(18, 1, '2015-08-11 14:47:44', '2015-08-11 12:47:44', '<section id="sectionContact" ng-controller="ContactCtrl">\r\n    <div class="container v-center">\r\n        <div class="row">\r\n            <div class="col-md-12">\r\n                <h1 class="text-center" ng-bind="''ContactUs'' | translate">&nbsp;</h1>\r\n                <hr>\r\n            </div>\r\n        </div>\r\n        <div class="row">\r\n            <div class="col-sm-9">\r\n                <form>\r\n                    <div class="row form-group">\r\n                        <div class="col-sm-5">\r\n                            <input type="text" class="form-control" name="name" placeholder="Name" required="">\r\n                        </div>\r\n                        <div class="col-sm-5">\r\n                            <input type="email" class="form-control" name="email" placeholder="Email" required="">\r\n                        </div>\r\n                    </div>\r\n                    <div class="row form-group">\r\n                        <div class="col-sm-10">\r\n                            <textarea type="homepage" class="form-control" placeholder="Information" required=""></textarea>\r\n                        </div>\r\n                    </div>\r\n                    <div class="row form-group">\r\n                        <div class="col-sm-10">\r\n                            <button class="btn btn-default btn-lg pull-right" ng-bind="''ContactUs'' | translate" ng-click="sendContactForm()">&nbsp;</button>\r\n                        </div>\r\n                    </div>\r\n                </form>\r\n            </div>\r\n            <div class="col-sm-3 pull-right">\r\n                <address>\r\n                    <strong>Le Chicche di Elena</strong><br>\r\n                    Via Roma, 34<br>\r\n                    Ravello SA<br>\r\n                    089 858285\r\n                </address>\r\n                <address>\r\n                    <strong>Email Us</strong><br>\r\n      