(24, 1, '2015-08-16 11:49:18', '2015-08-16 09:49:18', '<script>\r\nvar map;\r\nfunction initialize() {\r\n  var myLatlng = new google.maps.LatLng(40.650160,14.611770);\r\n  var mapOptions = {\r\n    zoom: 17,\r\n    center: myLatlng,\r\n    styles: [{\r\n      stylers: [{\r\n        saturation: -75\r\n      }]\r\n    }],\r\n    mapTypeControl: false,\r\n    zoomControl: false,\r\n    scrollwheel: false,\r\n    navigationControl: false,\r\n    scaleControl: false\r\n  }\r\n  var map = new google.maps.Map(document.getElementById(''map-canvas''), mapOptions);\r\n  var image = ''wp-content/themes/lechicchedielena/img/gmap-marker.png'';\r\n\r\n  var marker = new google.maps.Marker({\r\n      position: myLatlng,\r\n      map: map,\r\n      title: ''Le Chicche di Elena'',\r\n      icon: image\r\n  });\r\n}\r\n\r\ngoogle.maps.event.addDomListener(window, ''load'', initialize);\r\n</script>\r\n\r\n<section class="container-fluid" id="sectionMaps">\r\n    <div class="row">\r\n        <div class="col-md-12">\r\n            <h1 class="text-center" ng-bind="''Maps'' | translate">&nbsp;</h1>\r\n            <hr>\r\n        </div>\r\n    </div>\r\n\r\n    <div id="map-canvas"></div>\r\n</section>', 'sectionMaps', '', 'inherit', 'open', 'open', '', '22-revision-v1', '', '', '2015-08-16 11:49:18', '2015-08-16 09:49:18', '', 22, 'http://localhost/alefal.it/PROJECTS/wordpress-4.2.3/2015/08/16/22-revision-v1/', 0, 'revision', '', 0),
