(26, 1, '2015-08-16 11:52:23', '2015-08-16 09:52:23', '<section class="container-fluid" id="sectionCollection" ng-controller="CollectionsCtrl">\r\n    <div class="container v-center">\r\n        <div class="row">\r\n            <div class="col-md-12">\r\n                <h1 class="text-center" ng-bind="''Collection'' | translate">&nbsp;</h1>\r\n                <hr>\r\n            </div>\r\n        </div>\r\n\r\n        <div class="row">\r\n            <div class="col-sm-4" ng-repeat="element in listEvidenceCollections.posts">\r\n                <div class="row">\r\n                    <div class="col-sm-12 text-center">\r\n                        <div class="panel panel-default slideInLeft animate">\r\n                            <div class="panel-heading">\r\n                            <h3>{{element.title}}</h3></div>\r\n                            <div class="panel-body">\r\n                                <p ng-if="currentLanguage == ''it''" ng-bind-html="element.custom_fields.descrizione"></p>\r\n                                <p ng-if="currentLanguage == ''en''" ng-bind-html="element.custom_fields.description"></p>\r\n                                <p class="text-center">\r\n                                    <img ng-src="{{element.custom_fields.immagine[0]}}" class="img-responsive thumbnail center-block imgCollection" />\r\n                                </p>\r\n                                <button class="btn btn-danger" ng-click="openDetail(element.id,element.title,element.custom_fields.descrizione,element.custom_fields.description,element.custom_fields.immagine[0])" ng-bind="''Detail'' | translate">&nbsp;</button>\r\n                            </div>\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n            </div> \r\n        </div>\r\n\r\n        <br />\r\n\r\n        <div class="row">\r\n            <div class="col-sm-3" ng-repeat="element in listCollections.posts">\r\n                <div class="text-center">\r\n                    <a href="javascript:void(0);" title="Click for detail!" ng-click="openDetail(element.id,element.title,element.custom_fields.descrizione,element.custom_fields.description,element.custom_fields.immagine[0])">\r\n                        <img class="img-responsive thumbnail photoCollections" ng-src="{{element.custom_fields.immagine[0]}}">\r\n                    </a>\r\n                    <h3 class="text-center">{{element.title}}</h3>\r\n                </div>\r\n            </div>\r\n        </div>\r\n\r\n    </div>\r\n</section>', 'sectionCollection', '', 'trash', 'closed', 'open', '', 'sectioncollection', '', '', '2016-01-11 17:33:40', '2016-01-11 16:33:40', '', 0, 'http://localhost/alefal.it/PROJECTS/wordpress-4.2.3/?p=26', 0, 'post', '', 0),
(27, 1, '2015-08-16 11:52:23', '2015-08-16 09:52:23', '<section class="container-fluid" id="sectionCollection" ng-controller="CollectionsCtrl">\r\n    <div class="container v-center">\r\n        <div class="row">\r\n            <div class="col-md-12">\r\n                <h1 class="text-center" ng-bind="''Collection'' | translate">&nbsp;</h1>\r\n                <hr>\r\n            </div>\r\n        </div>\r\n\r\n        <div class="row">\r\n\r\n            <div class="col-sm-4" ng-repeat="element in listCollections | filter: { evidence: ''true'' }">\r\n                <div class="row">\r\n                    <div class="col-sm-12 text-center">\r\n                        <div class="panel panel-default slideInLeft animate">\r\n                            <div class="panel-heading">\r\n                            <h3>{{element.title}}</h3></div>\r\n                            <div class="panel-body">\r\n                                <p ng-if="currentLanguage == ''it''">{{element.descriptionIt}}</p>\r\n                                <p ng-if="currentLanguage == ''en''">{{element.descriptionEn}}</p>\r\n                                <p class="text-center">\r\n                                    <img src="{{element.image}}" class="img-responsive thumbnail center-block imgCollection" />\r\n                                </p>\r\n                                <!--\r\n                                <button class="btn btn-danger" data-toggle="modal" data-target="#myModal">Detail</button>\r\n                                -->\r\n                                <button class="btn btn-danger" ng-click="openDetail(element.id,element.title,element.descriptionIt,element.descriptionEn,element.image)" ng-bind="''Detail'' | translate">&nbsp;</button>\r\n                            </div>\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n            \r\n        </div>\r\n        <!--/row-->\r\n        \r\n        <br />\r\n\r\n        <div class="row">\r\n            <div class="col-sm-3" ng-repeat="element in listCollections | filter: { evidence: ''false'' }">\r\n                <div class="text-center">\r\n                    <a href="javascript:void(0);" title="Click for detail!" ng-click="openDetail(element.id,element.title,element.descriptionIt,element.descriptionEn,element.image)">\r\n                        <img class="img-circle img-responsive img-thumbnail photoCollections" src="{{element.image}}">\r\n                    </a>\r\n                    <h3 class="text-center">{{element.title}}</h3>\r\n                </div>\r\n            </div>\r\n        </div>\r\n        <!--/row-->\r\n    </div>\r\n</section>', 'sectionCollection', '', 'inherit', 'open', 'open', '', '26-revision-v1', '', '', '2015-08-16 11:52:23', '2015-08-16 09:52:23', '', 26, 'http://localhost/alefal.it/PROJECTS/wordpress-4.2.3/2015/08/16/26-revision-v1/', 0, 'revision', '', 0),
